import React, { useState } from "react";
import "../FilterCheckbox/FilterCheckbox.css";
import tumblerOn from "../../images/tumbler-on.svg"
import tumblerOff from "../../images/tumbler-off.svg"

function FilterCheckbox() {
    const [isActive, setIsActive] = useState(false);

    const toggleButton = () => {
      setIsActive(!isActive);
    };

    return (
        <div className="search-form__short-films">
            <div className="search-form__checkbox-container">
                <button onClick={toggleButton} className={`toggle-button ${isActive ? "toggle-button_active" : ""}`}>
                    {isActive ? <img  src={tumblerOn} alt="Включено" /> : <img  src={tumblerOff} alt="Выключено" />}
                </button>
                <p className="search-form__label">Короткометражки</p>
            </div>
        </div>
    )
}

export default FilterCheckbox;
